import React, { useState } from 'react'

const ChatBot = () => {
    const [message, setMessage] = useState("");
    const [chat, setChat] = useState([]);
    
    const handleChange = (e) => {
        setMessage(e.target.value);
        console.log(message);
    }
    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
          setChat([...chat, message]);
          setMessage("");
          console.log(chat);
        }
      };

  return (
    <div className='h-[calc(100vh-73px)] w-full px-10 flex min-h-fit bg-[rgb(43,47,79)] justify-between'>
        <div className='mt-10 w-full'>
            <input type="text" placeholder="ayo wassup ma home BOYY!!!" value={message} onChange={ handleChange } onKeyDown={ handleKeyDown } className='w-full h-fit px-5 py-3 rounded-sm outline-1 bg-gray-600 text-white focus:outline focus:outline-[rgb(0,255,123)] focus:shadow-[rgba(0,255,123,0.2)_0_0_10px_10px] transition-all duration-300'></input>
        </div>
    </div>
  )
}

export default ChatBot
