import React, { useState } from 'react'

const ChatBot = () => {
    const [message, setMessage] = useState({
        role: "",
        content: ""
    });
    const [chat, setChat] = useState([{
        role: "",
        content: ""
    }]);
    const [chatResponses, setChatResponses]= useState([]);
    
    const handleChange = (e) => {
        setMessage({role:"user", content:e.target.value});
    }
    const handleKeyDown = async (e) => {
        if (e.key === 'Enter') {
            setChat([...chat, {role:"user", content:e.target.value}]);

            try {
                const res = await fetch("http://localhost:5000/api/openai/chatGPT", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ chat: message }),
                });
                
                const data = await res.json();

                console.log("data", data.chatResponse)
                setChatResponses([...chatResponses, data.chatResponse]);
                console.log(chatResponses)
            } catch (error) {
                alert(error);
            }

          setMessage({role:"", content:""});
        }        
    };

  return (
    <div className='h-[calc(100vh-73px)] w-full flex min-h-fit bg-[#343541] justify-between'>
        <div className='w-full px-20 py-10'>
            <div className='bg-gray-100 w-fit px-5 py-3 rounded-r-lg rounded-t-lg'>
                {
                    (chat.length > 0) && (
                        chat.map((message, index) => (
                            <div key={index} style={{ textAlign: 'left' }}>
                                {message.content}
                            </div>
                        ))
                    )

                }
            </div>

            <div className='bg-[#10a37f] w-fit px-5 py-3 rounded-l-lg rounded-t-lg'>
                {
                    (chatResponses.length > 0) && (
                        chatResponses.map((message, index) => (
                            <div key={index} style={{ textAlign: 'right' }}>
                                {message}
                            </div>
                        ))
                    )
                }
            </div>
        </div>

        <div className='absolute bottom-0 bg-[#33363f] w-full py-3 flex justify-center border-t-[1px] border-[rgb(0,255,123)]'>
            <input type="text" placeholder="ayo wassup ma home BOYY!!!" value={message.content} onChange={ handleChange } onKeyDown={ handleKeyDown } className='w-[80%] h-fit px-5 py-3 rounded-sm outline-1 bg-[#40414f] text-white focus:outline focus:outline-[rgb(0,255,123)] focus:shadow-[rgba(0,255,123,0.2)_0_0_5px_5px] transition-all duration-300'></input>
        </div>
    </div>
  )
}

export default ChatBot
