import React, { useState } from 'react'

const ChatBot = () => {
    const [message, setMessage] = useState({
        role: "",
        content: ""
    });
    const [chat, setChat] = useState([{
        role: "",
        content: ""
    }]);
    const [chatResponses, setChatResponses]= useState([]);
    
    const handleChange = (e) => {
        setMessage({role:"user", content:e.target.value});
    }
    const handleKeyDown = async (e) => {
        if (e.key === 'Enter') {
            console.log(message);
            try {
                const res = await fetch("http://localhost:5000/api/openai/chatGPT", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ chat: message }),
                });
                
                const data = await res.json();
                console.log(data)

                setChatResponses([...chatResponses, data]);

            } catch (error) {
                alert(error);
            }

          setMessage({role:"", content:""});
        }        
    };

  return (
    <div className='h-[calc(100vh-73px)] w-full flex min-h-fit bg-[rgb(43,47,79)] justify-between'>
        <div className='absolute bottom-0 bg-gray-100 w-full py-3 flex justify-center border-t-2 border-gray-50'>
            <input type="text" placeholder="ayo wassup ma home BOYY!!!" value={message.content} onChange={ handleChange } onKeyDown={ handleKeyDown } className='w-[80%] h-fit px-5 py-3 rounded-sm outline-1 bg-gray-600 text-white focus:outline focus:outline-[rgb(0,255,123)] focus:shadow-[rgba(0,255,123,0.2)_0_0_10px_10px] transition-all duration-300'></input>
        </div>
    </div>
  )
}

export default ChatBot
